mapper.sql:
FROM pmp_user a
LEFT JOIN pmp_admin b ON a.admin_id=b.admin_id
LEFT JOIN pmp_course c ON a.course_id=c.course_id
LEFT JOIN pmp_teaching_room d ON a.room_id=d.room_id
LEFT JOIN pmp_user_education e ON a.education_id=e.education_id
LEFT JOIN pmp_user_pay_type f ON a.pay_id=f.pay_id
WHERE a.del_on=0

    AND d.admin_id = #{educationAdminId,jdbcType=BIGINT}


    AND a.education_id = #{educationId,jdbcType=BIGINT}


    AND a.room_id = #{roomId,jdbcType=BIGINT}


    AND (a.user_name LIKE CONCAT('%',#{keyWord},'%') OR a.phone_no LIKE CONCAT('%',#{keyWord},'%')
    OR a.fixed_telephone LIKE CONCAT('%',#{keyWord},'%') )


    AND a.gender = #{gender,jdbcType=BIT}


    AND a.email LIKE CONCAT('%',#{email},'%')


    AND a.wechat_no LIKE CONCAT('%',#{wechatNo},'%')


    AND a.qq LIKE CONCAT('%',#{qq},'%')


    AND a.identity_no LIKE CONCAT('%',#{identityNo},'%')


    AND a.user_type = #{userType,jdbcType=INTEGER}


    AND a.admin_id = #{salesAdminId,jdbcType=BIGINT}


    AND a.course_id = #{courseId,jdbcType=BIGINT}


    AND a.graduation_status = #{graduationStatus,jdbcType=INTEGER}


    AND a.create_time>=#{startTime,jdbcType=BIGINT}


    AND #{endTime,jdbcType=BIGINT}>=a.create_time

ORDER BY a.create_time DESC,a.admin_id,c.sort

mapper.select:
SELECT a.user_id FROM pmp_user a
WHERE  a.user_name=#{userName,jdbcType=VARCHAR}
AND a.identity_no=#{identityNo,jdbcType=VARCHAR} LIMIT 1;

SELECT a.education_id,a.education_name
FROM pmp_user_education a WHERE a.del_on=0
ORDER BY a.sort,a.education_id;

SELECT a.pay_id,a.pay_name
FROM pmp_user_pay_type a WHERE a.del_on=0
ORDER BY a.sort,a.pay_id;

SELECT a.user_id, a.education_id, a.pay_id, a.room_id, a.user_name, a.gender, a.nationality, a.birthday, a.phone_no, a.fixed_telephone, a.email, a.backup_email, a.wechat_no, a.qq, a.identity_no, a.zip_code, a.user_address, a.graduation_time, a.graduation_school, a.school_major, a.industry, a.com_name, a.com_department, a.com_position, a.invoice_header, a.invoice_code, a.user_type, a.admin_id, a.course_id, a.graduation_status, a.cert_no, a.cert_en_name, a.cert_en_pasw, a.cert_cn_name, a.cert_cn_pasw, a.remark, a.create_time,b.nickName salesAdminName,b.admin_id salesAdminId,c.course_name,d.room_name,e.education_name,f.pay_name, a.examination_time,a.pay_total,a.pay_time, (SELECT b_1.nickName FROM pmp_admin b_1 WHERE d.admin_id=b_1.admin_id) educationAdminName FROM pmp_user a LEFT JOIN pmp_admin b ON a.admin_id=b.admin_id LEFT JOIN pmp_course c ON a.course_id=c.course_id LEFT JOIN pmp_teaching_room d ON a.room_id=d.room_id LEFT JOIN pmp_user_education e ON a.education_id=e.education_id LEFT JOIN pmp_user_pay_type f ON a.pay_id=f.pay_id WHERE a.del_on=0
AND a.user_id = #{userId,jdbcType=BIGINT}
AND a.phone_no = #{phoneNo,jdbcType=VARCHAR}
AND a.identity_no = LIKE CONCAT('%',#{keyWord})
AND a.user_name = #{userName}
AND a.identity_no = #{identityNo}
